<!-- Modal -->
<div class="modal fade new-delivery-person" tabindex="-1" aria-hidden="true">
    <div class="modal-dialog modal-dialog-centered">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title">New Delivery Person</h5>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <form id="new-delivery-person">
                <div class="modal-body">
                    <div class="row g-3">
                        <div class="col-md-12">
                            <label for="sdsds" class="form-label">Name</label>
                            <div class="position-relative input-icon">
                                <input type="text" class="form-control" id="sdsds" name="name">
                                <span class="position-absolute top-50 translate-middle-y"><i class="lni lni-user"></i></span>
                            </div>
                        </div>
                        <div class="col-md-12">
                            <label for="sdgfgds" class="form-label">Mobile</label>
                            <div class="position-relative input-icon">
                                <input type="number" maxlength="12" class="form-control no-arrow" id="sdgfgds" name="mobile_number_1">
                                <span class="position-absolute top-50 translate-middle-y"><i class="lni lni-phone"></i></span>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>
                    <button type="submit" class="btn btn-success">Save</button>
                </div>
            </form>
        </div>
    </div>
</div>
